<?	
	/**
	* Register Error Exeption Function
	*/
	register_shutdown_function('ErrorExceptionHandler');
	
	/**
	* Error Class
	*
	* @link http://orionsoft.co.in/
	* @copyright 2010-2011 Orion Software Pvt. Ltd.
	* @author Susanta Das <susanta.das@orionsoft.co.in>
	* @version 1.0.50
	*/
	class Error {
		public function raise($message, $caption='Message') {	
			?>
				<!DOCTYPE html>
				<html>
					<head>
						<meta http-equiv="Content-type" content="text/html; charset=UTF-8">
						<title>Emerald - <?=$caption?></title>						
						<link href="data:image/png;base64," rel="shotcut icon">	
						
						<style type="text/css" media="screen">
							#container *{
								font-family: Arial, sans-serif;
								text-decoration: none;
							}
							
							#container { 
								color:#393939;	
								/*background: #F5F5F5;
								border:1px solid #A8A8A8;*/
								width: auto;
								text-align: center;
							}
							
							#container h1{
								color:#f00;
								font-size: 30px;
								font-weight: bold;
							}
							
							#container p{
								color:#000;
								font-size: 12px;
								font-weight: normal;
							}
														
							#suggestions {								
								margin-top: 35px;
							}
							
							#suggestions a {								
								font-size: 12px;
								font-weight: normal;								
								margin: 0 10px;
							}
							
							#logo {
								margin:12px;
							}							
						</style>
					</head>
				  
					<body>
						<div id="container">
							<h1><?=$caption?></h1>
							<p><?=$message?></p>

							<p><em>Are you trying to access the object?</em>
								It may not be created or formed.
							</p>

							<p>
								Learn how to use <strong>Emerald Framework</strong><br>
								for developing high end web based application.
							</p>

							<a target="_blank">
								<img id="logo" src="data:image/png;base64,">
							</a>
						</div>
					</body>
				</html>
			<?			
			
			exit;
		}
		
		/**
		* Get Error Type
		*
		* Function GetErrorType
		* @param $type as string
        * @return string
		* @author Susanta Das
		*/
		public function GetErrorType($type) 
		{ 
			$return = ""; 
			
			if($type & E_ERROR) // 1 // 
				$return .= '& Fatal Error '; 
			if($type & E_WARNING) // 2 // 
				$return .= '& E_WARNING '; 
			if($type & E_PARSE) // 4 // 
				$return .= '& E_PARSE '; 
			if($type & E_NOTICE) // 8 // 
				$return .= '& E_NOTICE '; 
			if($type & E_CORE_ERROR) // 16 // 
				$return .= '& E_CORE_ERROR '; 
			if($type & E_CORE_WARNING) // 32 // 
				$return .= '& E_CORE_WARNING '; 
			if($type & E_COMPILE_ERROR) // 64 // 
				$return .= '& E_COMPILE_ERROR '; 
			if($type & E_COMPILE_WARNING) // 128 // 
				$return .= '& E_COMPILE_WARNING '; 
			if($type & E_USER_ERROR) // 256 // 
				$return .= '& E_USER_ERROR '; 
			if($type & E_USER_WARNING) // 512 // 
				$return .= '& E_USER_WARNING '; 
			if($type & E_USER_NOTICE) // 1024 // 
				$return .= '& E_USER_NOTICE '; 
			if($type & E_STRICT) // 2048 // 
				$return .= '& E_STRICT '; 
			if($type & E_RECOVERABLE_ERROR) // 4096 // 
				$return .= '& E_RECOVERABLE_ERROR '; 
			if($type & E_DEPRECATED) // 8192 // 
				$return .= '& E_DEPRECATED '; 
			if($type & E_USER_DEPRECATED) // 16384 // 
				$return .= '& E_USER_DEPRECATED '; 
			
			return trim(substr($return, 2)); 
		}
	}
	
	/**
	* Error Exception Handler
	*
	* Function ErrorExceptionHandler
	* @author Susanta Das
	*/
	function ErrorExceptionHandler() {
		// get system error
		$error = error_get_last();
		
		// show the error
		if(!empty($error['message']) && $error['type'] != E_NOTICE && $error['type'] != E_STRICT && $error['type'] != E_WARNING)
		Error::raise($error['message'].' in <strong>'.$error['file'].'</strong> on line <strong>'.$error['line'].'</strong>', Error::GetErrorType($error['type']));		
	}
?>